color-contrast.code_example:
/* Vorher: Kontrast 2.5:1 */
color: #999;
background: #fff;

/* Nachher: Kontrast 12.6:1 */
color: #333;
background: #fff;

css-orientation-lock.code_example:
/* Vermeiden */
@media (orientation: portrait) {
  body { transform: rotate(90deg); }
}

/* Besser: Beide Orientierungen unterstützen */
@media (orientation: portrait) { /* Anpassungen */ }
@media (orientation: landscape) { /* Anpassungen */ }

link-in-text-block.code_example:
/* Falsch: Nur Farbe */
a { color: blue; text-decoration: none; }

/* Richtig: Mit Unterstreichung */
a { color: blue; text-decoration: underline; }

/* Oder mit anderer Kennzeichnung */
a { color: blue; border-bottom: 1px solid; }
